import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-big-card',
  templateUrl: './big-card.component.html',
  styleUrls: ['./big-card.component.css']
})
export class BigCardComponent {
  @Input()
  photoCover:string = "";
  @Input()
  cardTitle:string = "";
  @Input()
  cardDescription:string = "";
  @Input()
  Id:string = "0";
  constructor(){}
  ngOnInit():void {}
}
<div class="big-card">
    <div class="photo"><img src="{{photoCover}}" alt="" srcset="" height="400px"></div>
    <div class="title"><h1><a href="#">{{cardTitle}}</a></h1></div>
    <div class="description"><p><a href="#">{{cardDescription}}</a></p></div>
</div>
